/**
Check if a number is [positive zero](https://en.wikipedia.org/wiki/Signed_zero).

@example
```
import positiveZero = require('positive-zero');

positiveZero(0);
//=> true

positiveZero(+0);
//=> true

positiveZero(-0);
//=> false
```
*/
declare function positiveZero(number: number): boolean;

export = positiveZero;
